import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { ReactComponent as CloseLogo } from '../../images/close_black_24dp.svg';

export default function ErrorPopUp({ gotError, ...msg }) {
  const [errorMsg] = useState(msg);
  const [errorPassed, setErrorPassed] = useState(gotError);

  if (!errorPassed) return null;
  return ReactDOM.createPortal(
    <div className="error-pop-up">
      <CloseLogo className="close-err-msg" onClick={() => setErrorPassed(false)} />
      <h1>Oops, there was an error!</h1>
      {Array.isArray(errorMsg)
      && errorMsg.array.forEach((element) => `<p>${element}<p>`)}
    </div>,
    document.getElementById('portal'),
  );
}
