/* eslint-disable no-unused-vars */
import React, { useState, useEffect } from 'react';
import ReactFlow, { Position } from 'react-flow-renderer';
import format from 'date-fns/format';
import parseISO from 'date-fns/parseISO';
import LoadingSpinner from './LoadingSpinner';

export default function Profile() {
  const [applications, setApplications] = useState([]);
  const [interviews, setInterviews] = useState([]);
  const [elements, setElements] = useState([]);
  const [connections, setConnections] = useState([]);
  const [loaded, setLoaded] = useState(false);

  const createApplicationsElements = () => {
    const newArray = applications.map((application, index) => ({
      id: application._id,
      type: 'input',
      data: {
        label: `Listing at ${application.company_name}`,
      },
      position: { x: 250 + 200 * index, y: 20 },
      draggable: false,
      isHidden: false,
    }));
    return newArray;
  };

  const createInterviewsElements = () => {
    const newArray = interviews.map((interview, index) => ({
      id: interview._id,
      type: 'output',
      data: {
        label: `Interview on ${format(parseISO(interview.date), 'yyyy-MM-dd')}`,
      },
      position: { x: 250 + 100 * index, y: 100 },
      draggable: false,
      isHidden: false,
    }));
    return newArray;
  };

  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_SERVER}profile`, {
      method: 'GET',
      credentials: 'include',
    })
      .then((res) => res.json())
      .then((json) => {
        if (typeof json.err !== 'undefined') {
          console.log('Fetching error', json.err);
        } else {
          setApplications(json.applications);
          setInterviews(json.interviews);
          setElements(createApplicationsElements());
          setElements((oldarray) => [...oldarray, createInterviewsElements()]);
          setLoaded(true);
        }
      })
      .catch((err) => console.log('error: ', err));
  }, [loaded]);

  return (
    loaded
      ? (
        <ReactFlow elements={elements} />
      )
      : <LoadingSpinner />
  );
}
