/* eslint-disable no-unused-vars */
import React, { useState, useEffect } from 'react';
import ReactFlow, { Position } from 'react-flow-renderer';
import LoadingSpinner from './LoadingSpinner';

export default function Profile() {
  const [applications, setApplications] = useState([]);
  const [interviews, setInterviews] = useState([]);
  const [elements, setElements] = useState([]);
  const [loaded, setLoaded] = useState(false);

  const createApplicationElements = () => {
    const newArray = applications.map((application, index) => ({
      id: application._id,
      type: 'input',
      data: {
        label: `Listing at ${application.company_name}`,
      },
      position: { x: 250 + 200 * index, y: 20 },
      draggable: false,
      isHidden: false,
    }));
    console.log(newArray);
    return newArray;
  };

  useEffect(() => {
    fetch(`${process.env.REACT_APP_API_SERVER}profile`, {
      method: 'GET',
      credentials: 'include',
    })
      .then((res) => res.json())
      .then((json) => {
        if (typeof json.err !== 'undefined') {
          console.log('Fetching error', json.err);
        } else {
          setApplications(json.applications);
          setInterviews(json.interviews);
        }
      })
      .catch((err) => console.log('error: ', err))
      .finally(() => {
        setElements(createApplicationElements());
        setLoaded(true);
      });
  }, []);

  return (
    loaded
      ? (
        <ReactFlow elements={elements} />
      )
      : <LoadingSpinner />
  );
}
